import React, { useState, useEffect } from 'react';

const MeetupDisplay = () => {
  const [groupedMeetups, setGroupedMeetups] = useState({});
  const [expandedDescriptions, setExpandedDescriptions] = useState({});

  useEffect(() => {
    fetch('/meetup.json')
      .then(response => response.json())
      .then(data => {
        const allItems = data.flatMap(group => 
          group.items.map(item => ({ ...item, groupTitle: group.group_title, groupLink: group.group_link }))
        );

        const groupedByDate = allItems.reduce((acc, item) => {
          const date = new Date(item.time).toDateString(); // Extract just the date part
          if (!acc[date]) {
            acc[date] = [];
          }
          acc[date].push(item);
          return acc;
        }, {});

        setGroupedMeetups(groupedByDate);
      })
      .catch(error => console.error('Error loading meetups:', error));
  }, []);

  const toggleDescription = (date, idx) => {
    setExpandedDescriptions(prev => ({
      ...prev,
      [date]: { ...prev[date], [idx]: !prev[date]?.[idx] }
    }));
  };

  return (
    <div>
      {Object.entries(groupedMeetups).map(([date, items]) => (
        <div key={date}>
          <h2>{date}</h2>
          {items.map((item, idx) => (
            <div key={idx}>
              <p>Time: {new Date(item.time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
              <p>Title: <a href={item.event_link} target="_blank" rel="noopener noreferrer">{item.title}</a></p>
              <p>Location: {item.location}</p>
              <p>Group: <a href={item.groupLink} target="_blank" rel="noopener noreferrer">{item.groupTitle}</a></p>
              <p style={{ cursor: 'pointer' }} onClick={() => toggleDescription(date, idx)}>
                Description {expandedDescriptions[date]?.[idx] ? '(less)' : '(more)'}
              </p>
              {expandedDescriptions[date]?.[idx] && <p>{item.description}</p>}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default MeetupDisplay;
