import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BsGeoAlt } from "react-icons/bs"; 

const MeetupDisplay = () => {
  const [groupedMeetups, setGroupedMeetups] = useState({});
  const [expandedDescriptions, setExpandedDescriptions] = useState({});

  useEffect(() => {
    fetch('/meetup.json')
      .then(response => response.json())
      .then(data => {
        const allItems = data.flatMap(group => 
          group.items.map(item => ({ ...item, groupTitle: group.group_title, groupLink: group.group_link }))
        );

        const groupedByDate = allItems.reduce((acc, item) => {
          const date = new Date(item.time).toDateString(); // Extract just the date part
          if (!acc[date]) {
            acc[date] = [];
          }
          acc[date].push(item);
          return acc;
        }, {});

        // Sort events for each date by time
        for (const date in groupedByDate) {
          groupedByDate[date].sort((a, b) => new Date(a.time) - new Date(b.time));
        }

        setGroupedMeetups(groupedByDate);
      })
      .catch(error => console.error('Error loading meetups:', error));
  }, []);

  const toggleDescription = (date, idx) => {
    setExpandedDescriptions(prev => ({
      ...prev,
      [date]: { ...prev[date], [idx]: !prev[date]?.[idx] }
    }));
  };

  return (

    <div className="container my-4">
      {Object.entries(groupedMeetups).map(([date, items]) => (
        <div key={date} className="mb-3">
          <h2 className="mb-2">{date}</h2>
          <div className="row">
            {items.map((item, idx) => (
              <div key={idx} className="col-sm-6 d-flex align-items-stretch">
                <div className="card mb-2" style={{ minWidth: '100%' }}>
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <div className="badge bg-dark text-white" style={{ width:'80px' }}>
                        {new Date(item.time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </div><div style={{  width: 'calc(100% - 80px)' }}>
                      <h4 >{item.title}</h4></div>
                    </div>
                    <p style={{ marginLeft: '80px' }} className="card-text"><BsGeoAlt /> {item.location}</p>
                    <p className="card-text" style={{ cursor: 'pointer', marginLeft: '80px' }} onClick={() => toggleDescription(date, idx)}>
                      Description {expandedDescriptions[date]?.[idx] ? '(less)' : '(more)'}
                    </p>
                    {expandedDescriptions[date]?.[idx] && <p style={{ marginLeft: '80px' }} className="card-text">{item.description}</p>}
                    <div className="text-muted small">
                      <p style={{ marginLeft: '80px' }}>Group: <a href={item.groupLink} target="_blank" rel="noopener noreferrer">{item.groupTitle}</a></p>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};



export default MeetupDisplay;
