import React, { useState, useEffect } from 'react';

const MeetupDisplay = () => {
  const [meetups, setMeetups] = useState([]);
  const [expandedDescriptions, setExpandedDescriptions] = useState({});

  useEffect(() => {
    fetch('/meetup.json')
      .then(response => response.json())
      .then(data => {
        const allItems = data.flatMap(group => 
          group.items.map(item => ({ ...item, groupTitle: group.group_title, groupLink: group.group_link }))
        );
        allItems.sort((a, b) => new Date(a.time) - new Date(b.time));
        setMeetups(allItems);
      })
      .catch(error => console.error('Error loading meetups:', error));
  }, []);

  const toggleDescription = idx => {
    setExpandedDescriptions(prev => ({ ...prev, [idx]: !prev[idx] }));
  };

  return (
    <div>
      {meetups.map((item, idx) => (
        <div key={idx}>
          <p>Time: {item.time}</p>
          <p>Title: <a href={item.event_link} target="_blank" rel="noopener noreferrer">{item.title}</a></p>
          <p>Location: {item.location}</p>
          <p>Group: <a href={item.groupLink} target="_blank" rel="noopener noreferrer">{item.groupTitle}</a></p>
          <p style={{ cursor: 'pointer' }} onClick={() => toggleDescription(idx)}>Description {expandedDescriptions[idx] ? '(less)' : '(more)'}</p>
          {expandedDescriptions[idx] && <p>{item.description}</p>}
        </div>
      ))}
    </div>
  );
};

export default MeetupDisplay;
